import React from 'react';

const [button, setbutton] = useState(view);
const buttoonActions = () => {
    if (button === 'view') {
        return <AdminActions />;
    } else if (role === 'search') {
        return <CustomerActions />;
    }
    else if (role === 'purchase') {
        return <CustomerActions />;
    }
};

const CustomerActions = () => {
    return (
        <div>
            <div>
                <h2>Customer Actions</h2>
                <button>View Available Books</button>
                <button>Search Books</button>
                <button>Purchase Books</button>
            </div>
            <div>
                {buttoonActions()}
            </div>
        </div>
    );
};

export default CustomerActions;
