import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import imglogo from './book.png'
import 'bootstrap/dist/css/bootstrap.css';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <div class="row grow w-100">
      <div class="col-12 bg-primary py-3">
      <h1>Alpine Book Management</h1>
      <p>A <b>complete</b>  store for all books.</p>
      </div>
      <div class="col-4 bg-info py-3">
      <img src={imglogo}  style={{height:"500px"}}/>
      </div>
      <div class="main col-8 bg-warning  py-3">
      <App />  
       </div>
    </div>
    <div class="row w-100">
      <div class="col-12 py-3 bg-danger">
      <h4 style={{ marginTop: "5px" }}> Developed by (1) Devam Yogeshkumar Patel (2) Subham Goyal & (3) Yash Rashtogi</h4>

      </div>
    </div>





  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
