import React, { useState, useEffect } from 'react';

const UserSearchBook = () => {
    const [books, setBooks] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [filterOption, setFilterOption] = useState('title'); // Default filter

    useEffect(() => {
        // Simulate fetching data from an API or database
        const fetchBooks = async () => {
            const response = await fetch('http://127.0.0.1:5000/api/books'); // Adjust the API endpoint as needed
            const data = await response.json();
            setBooks(data);
        };

        fetchBooks();
    }, []);

    const handleSearch = () => {
      
        return books.filter(book => {
            const searchValue = book[filterOption].toLowerCase();
            return searchValue.includes(searchTerm.toLowerCase());
        });
    };

    return (
        <div>
            <h3>Book Search</h3>
            
            <div>
                <select onChange={(e) => setFilterOption(e.target.value)} value={filterOption}>
                    <option value="title">Title</option>
                    <option value="genre">Genre</option>
                    <option value="author">Author</option>
                    <option value="publication">Publication</option>
                </select>
                <input
                    type="text"
                    placeholder="Search..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                {/*<button onClick={handleSearch}>Search</button>*/}
            </div>
            <h3></h3>
            <table style={{ border: "1px solid black" }}>
                <tr>
                    <td style={{ border: "1px solid black" }}>Title</td>
                    <td style={{ border: "1px solid black" }}>genre</td>
                    <td style={{ border: "1px solid black" }}>author</td>
                    <td style={{ border: "1px solid black" }}>publication</td>
                    <td style={{ border: "1px solid black" }}>quantity</td>
                    <td style={{ border: "1px solid black" }}>price</td>                    
                </tr>
                {handleSearch().map(book => (
                    <tr id={book.id} style={{ border: "1px solid black" }}>
                        <td style={{ border: "1px solid black" }}>{book.title}</td>
                        <td style={{ border: "1px solid black" }}>Genre: {book.genre}</td>
                        <td style={{ border: "1px solid black" }}>Author: {book.author}</td>
                        <td style={{ border: "1px solid black" }}>Publication: {book.publication}</td>
                        <td style={{ border: "1px solid black" }}>>Quantity: {book.quantity}</td>
                        <td style={{ border: "1px solid black" }}>Price: â‚¹{book.price}</td>
                     </tr>
                ))}
            </table>
        </div>
    );
};

export default UserSearchBook;
